mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE USER_EMAIL = #{checkId}

SELECT COUNT(*)
FROM MEMBER
WHERE USER_NICKNAME = #{checkNickname}

SELECT USER_EMAIL
FROM MEMBER
WHERE USER_NAME = #{userName}
AND USER_NICKNAME = #{userNickname}
AND STATUS = 'Y'

SELECT USER_PWD
FROM MEMBER
WHERE USER_EMAIL = #{userEmail}
AND USER_NAME = #{userName}
AND USER_NICKNAME = #{userNickname}
AND STATUS = 'Y'
